<?php/** * @package App Image Uploader */namespace Inc;class Activate {	public static function activate() {		flush_rewrite_rules();		if ( ! current_user_can( 'activate_plugins' ) ) {			return;		}		// Insert DB Tables		init_app_notification_db();		// Adding option to redirect		add_option( 'app-image-uploader_do_activation_redirect', true );	}}// Function to create custom table for app notificationfunction init_app_notification_db() {	// WP Globals	global $table_prefix, $wpdb;	// App notification table name	$notificationTable = $table_prefix . 'appnotification';	// Create notification Table if not exist	if ( $wpdb->get_var( "show tables like '$notificationTable'" ) != $notificationTable ) {		// Query - Create Table		$sql = "CREATE TABLE `$notificationTable` (";		$sql .= " `id` int(11) NOT NULL auto_increment, ";		$sql .= " `title` varchar(500) NOT NULL, ";		$sql .= " `message` varchar(500) NOT NULL, ";		$sql .= " `image` varchar(500), ";		$sql .= " `url` varchar(500) NOT NULL, ";		$sql .= " `dtime` DateTime(6), ";		$sql .= " PRIMARY KEY `customer_id` (`id`) ";		$sql .= ") ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1;";		// Include Upgrade Script		require_once( ABSPATH . '/wp-admin/includes/upgrade.php' );		// Create Table		dbDelta( $sql );	}}